Workbook wb = Workbook.getWorkbook(new File("E:\\Softwares\\My PC Apps\\SoapUI\\Test Data\\SOAPData.xls"))
Sheet sht = wb.getSheet(0)

def rowCount = sht.getRows()
def colCount = sht.getColumns()

for(i=1; i<rowCount; i++){

	number = sht.getCell(0,i).getContents().toString()		//(col, row) format
	expectedWord = sht.getCell(1,i).getContents().toString()

	testRunner.testCase.setPropertyValue("Number", number)
	testRunner.testCase.setPropertyValue("ExpectedWord", expectedWord)

//	log.info(number)
//	log.info(expectedWord)

	testRunner.runTestStepByName('NumberToWords')

